mapper.update:
UPDATE journal
	SET journalTitle=#{journalTitle}
WHERE journalNo= #{journalNo}

UPDATE exercise
	SET exerciseCate =#{exerciseCate},  exerciseName = #{exerciseName}, exerciseWeight= #{exerciseWeight},
	exerciseSet=#{exerciseSet}, exerciseRep=#{exerciseRep}
WHERE exerciseNo = #{exerciseNo}
